import json, os

class FileReadTool(Component):
    display_name = "File Read Tool"
    description = "Reads a JSON/text file from the container path"
    icon = "document"

    inputs = [
        StrInput(
            name="path",
            display_name="File path",
            info="Container path to file (e.g., /app/data/converting.json)",
            value="/app/data/converting.json",
            tool_mode=True,  # делает компонент инструментом для агента
        ),
        StrInput(
            name="format",
            display_name="Format (auto/json/text)",
            value="auto",
        ),
    ]

    outputs = [
        Output(display_name="Data", name="output", method="run", type="Data"),
    ]

    def run(self) -> Data:
        path = self.path
        if not os.path.exists(path):
            return Data(data={"error": f"not found: {path}"})
        with open(path, "r", encoding="utf-8") as f:
            content = f.read()

        fmt = (self.format or "auto").lower()
        if fmt == "json" or (fmt == "auto" and path.endswith(".json")):
            try:
                return Data(data=json.loads(content))
            except Exception as e:
                return Data(data={"error": f"invalid json: {e}", "raw": content})
        return Data(data={"text": content})

