def set_advanced_true(component_input):
    component_input.advanced = True
    return component_input

class GigaChatAgentComponent(LCToolsAgentComponent):
    display_name: str = "GigaChat Agent"
    description: str = "Agent using GigaChat with tools, memory, and current date."
    icon = "bot"
    beta = False
    name = "GigaChatAgent"

    memory_inputs = [set_advanced_true(component_input) for component_input in MemoryComponent().inputs]

    model_list = ['GigaChat-2', 'GigaChat-2-Pro', 'GigaChat-2-Max']
    scope_list = ['GIGACHAT_API_PERS', 'GIGACHAT_API_CORP', 'GIGACHAT_API_B2B']

    inputs = [
         MessageTextInput(
            name="giga_api", 
            display_name="GigaChat API Key",
            info="API key for GigaChat authentication"
        ),
        DropdownInput(
            name="model_name", 
            display_name="Model", 
            options=model_list, 
            value="GigaChat",
            info="Select GigaChat model version"
        ),
        DropdownInput(
            name="scope", 
            display_name="Scope", 
            options=scope_list, 
            value="GIGACHAT_API_PERS",
            info="API authentication scope"
        ),
        MultilineInput(
            name="system_prompt",
            display_name="Agent Instructions",
            value="Ты — умный и профессиональный ассистент. Отвечай понятно и структурированно.",
            info="Initial instructions for the agent"
        ),
        IntInput(
            name="n_messages",
            display_name="Chat History Length",
            value=100,
            advanced=True,
            info="Number of historical messages to include"
        ),
        MessageTextInput(
            name="temperature",
            display_name="Temperature",
            value="0.1",
            info="Controls randomness (0-1)"
        ),
        MessageTextInput(
            name="max_tokens",
            display_name="Max Tokens",
            value="500",
            info="Maximum response length"
        ),
        StrInput(name="if_else_input", display_name="If-else input", input_types=["str", "Message"]),
        BoolInput(
            name="add_current_date_tool",
            display_name="Add Date Tool",
            value=True,
            advanced=True,
            info="Include current date tool"
        ),
        *LCToolsAgentComponent._base_inputs,
    ]

    outputs = [Output(name="response", display_name="Response", method="message_response")]

    def tool_stringify_proxy(self, tool: StructuredTool) -> StructuredTool:
        original = tool.func
        def wrapped(*args, **kwargs):
            out = original(*args, **kwargs)
            try:
                # Langflow Message-like
                if hasattr(out, "text"):
                    return str(out.text or "")
                # Langflow Data-like
                if hasattr(out, "data"):
                    return str(out.data)
                # Любой не-строковый объект (dict, list, etc.)
                if not isinstance(out, str):
                    return str(out)
                return out
            except Exception:
                return str(out)
        tool.func = wrapped
        return tool


    async def message_response(self) -> Message:
        try:

            llm = GigaChat(
                credentials=self.giga_api,
                model=self.model_name,
                scope=self.scope,
                temperature=float(self.temperature) if hasattr(self, 'temperature') else 0.1,
                max_tokens=int(self.max_tokens) if hasattr(self, 'max_tokens') else 500,
                verify_ssl_certs=False
            )


            chat_history = await self.get_memory_data()
            if chat_history is None:
                chat_history = []


            tools = []
            if hasattr(self, 'tools') and self.tools:
                tools.extend([t for t in self.tools if isinstance(t, StructuredTool)])

            tools = [self.tool_stringify_proxy(t) for t in tools]

            if getattr(self, 'add_current_date_tool', False):
                try:
                    current_date_tool = (await CurrentDateComponent(**self.get_base_args()).to_toolkit())[0]
                    if isinstance(current_date_tool, StructuredTool):
                        tools.append(current_date_tool)
                except Exception as e:
                    logger.warning(f"Failed to add current date tool: {str(e)}")


            functions = []
            if tools:
                try:
                    functions = [format_tool_to_openai_function(t) for t in tools if t is not None]
                except Exception as e:
                    logger.error(f"Error formatting tools: {str(e)}")
                    functions = []


            prompt_messages = [
                SystemMessage(content=str(self.system_prompt) if hasattr(self, 'system_prompt') else "")
            ]

            if chat_history:
                prompt_messages.extend(self._convert_messages(chat_history))

            prompt_messages.extend([
                ("human", "{input}"),
                MessagesPlaceholder(variable_name="agent_scratchpad")
            ])

            prompt = ChatPromptTemplate.from_messages(prompt_messages)

            input_text = ""
            if hasattr(self, 'input_value'):
                if isinstance(self.input_value, str):
                    input_text = self.input_value
                elif hasattr(self.input_value, 'text'):
                    input_text = str(self.input_value.text)
                else:
                    input_text = str(self.input_value)


            agent = {
                "input": lambda x: x["input"],
                "chat_history": lambda x: x["chat_history"],
                "agent_scratchpad": lambda x: format_to_openai_function_messages(x["intermediate_steps"]),
            } | prompt | llm.bind(functions=functions if functions else None) | OpenAIFunctionsAgentOutputParser()

            agent_executor = AgentExecutor(agent=agent, tools=tools, verbose=True)
            result = await agent_executor.ainvoke({
                "input": input_text,
                "chat_history": self._convert_messages(chat_history)
            })
            output_response = str(result.get("output", ""))
            if output_response:
                return Message(text=output_response)
            else: 
                return Message(text='Агент не нашел верный ответ')


        except Exception as e:
            logger.error(f"Error in GigaChatAgent: {str(e)}")
            return Message(text=f"An error occurred: {str(e)}")

    def _convert_messages(self, messages: List[Message]) -> List[Union[HumanMessage, AIMessage]]:
        """Safely convert Langflow Messages to LangChain Messages"""
        converted = []
        if not messages:
            return converted

        for msg in messages:
            try:
                content = str(getattr(msg, "text", ""))
                if hasattr(msg, "is_user_message") and msg.is_user_message:
                    converted.append(HumanMessage(content=content))
                elif hasattr(msg, "is_bot_message") and msg.is_bot_message:
                    converted.append(AIMessage(content=content))
                else:
                    converted.append(HumanMessage(content=content))
            except Exception as e:
                logger.warning(f"Failed to convert message: {str(e)}")
                continue
        return converted

    async def get_memory_data(self) -> List[Message]:
        """Safely retrieve memory data"""
        try:
            memory_component = MemoryComponent(**self.get_base_args())
            if hasattr(self, 'n_messages'):
                memory_component.n_messages = int(self.n_messages)
            messages = await memory_component.set(
                session_id=getattr(self.graph, "session_id", None),
                order="Ascending"
            ).retrieve_messages()
            return [msg for msg in messages if getattr(msg, "id", None) != getattr(getattr(self, "input_value", None), "id", None)]
        except Exception as e:
            logger.error(f"Error retrieving memory: {str(e)}")
            return []

