from typing import Any, Dict
from langflow.custom import Component
from langflow.io import MessageTextInput, Output, DropdownInput, StrInput, CodeInput
from langflow.schema import Data
import json, requests, uuid

JSON_TYPE_MAP = {
    "str": "string",
    "string": "string",
    "int": "integer",
    "integer": "integer",
    "float": "number",
    "number": "number",
    "bool": "boolean",
    "boolean": "boolean",
    "list": "array",
    "array": "array",
    "dict": "object",
    "object": "object",
}

class GChatStructured(Component):
    display_name = "GigaChat Structured Output"
    description = "Structured output via Function Calling. If schema isn't valid, answer will be: {'answer': 'str'}"
    documentation: str = "http://docs.langflow.org/components/custom"
    icon = "custom_components"

    model_list = ["GigaChat-2", "GigaChat-2-Pro", "GigaChat-2-Max"]
    scope_list = ["GIGACHAT_API_PERS", "GIGACHAT_API_CORP", "GIGACHAT_API_B2B"]

    inputs = [
        MessageTextInput(name="input_message", display_name="Input Message"),
        StrInput(name="giga_api", display_name="GigaChat API Key (Base64 credentials)", input_types=["str","Message"]),
        DropdownInput(name="model_name", display_name="Model", options=model_list, value="GigaChat-2"),
        DropdownInput(name="scope", display_name="Scope", options=scope_list, value="GIGACHAT_API_PERS"),
        CodeInput(
            name="output_schema",
            display_name="Output Schema (JSON or simplified)",
            info='Either full JSON Schema properties map, or simplified types like {"answer":"str","confidence":"float","skills":"array"}',
            value='{"answer":"str","confidence":"float","skills":{"type":"array","items":{"type":"string"}}}'
        ),
        MessageTextInput(name="function_name", display_name="Function Name", value="extract_data"),
        MessageTextInput(name="function_description", display_name="Function Description", value="Extract structured data from the input"),
        MultilineInput(name="system_prompt", display_name="System Prompt", value="Ты — умный ассистент. Извлекай данные в заданной схеме."),
        MessageTextInput(name="temperature", display_name="Temperature", value="0.1"),
    ]

    outputs = [Output(display_name="Structured Output", name="output", method="build_output", type="Data")]

    def _get_text(self, v: Any) -> str:
        try:
            if hasattr(v, "text"):
                return v.text or ""
        except Exception:
            pass
        if isinstance(v, dict) and "text" in v:
            return str(v.get("text") or "")
        return str(v or "")

    def _get_access_token(self, credentials: str, scope: str) -> str:
        url = "https://ngw.devices.sberbank.ru:9443/api/v2/oauth"
        headers = {
            "Content-Type": "application/x-www-form-urlencoded",
            "Accept": "application/json",
            "RqUID": str(uuid.uuid4()),
            "Authorization": f"Basic {credentials}",
        }
        resp = requests.post(url, headers=headers, data={"scope": scope}, timeout=30, verify=False)
        resp.raise_for_status()
        return resp.json()["access_token"]

    def _normalize_properties(self, schema_like: Dict) -> Dict:
        props = {}
        required = []
        for name, spec in schema_like.items():
            if isinstance(spec, dict) and "type" in spec:
                t = JSON_TYPE_MAP.get(str(spec["type"]).lower()) or "string"
                out = dict(spec)
                out["type"] = t
                if t == "array" and "items" not in out:
                    out["items"] = {"type": "string"}
                props[name] = out
            else:
                t = JSON_TYPE_MAP.get(str(spec).lower(), "string")
                props[name] = {"type": t}
                if t == "array":
                    props[name]["items"] = {"type": "string"}
            # делаем ключ обязательным
            required.append(name)
        return {"type": "object", "properties": props, "required": required}


    def _build_function(self, name: str, description: str, schema_input: Any) -> Dict:
        if isinstance(schema_input, str):
            try:
                schema_like = json.loads(schema_input)
            except json.JSONDecodeError:
                # трактуем как один строковый ответ
                schema_like = {"answer": "str"}
        elif isinstance(schema_input, dict):
            schema_like = schema_input
        else:
            schema_like = {"answer": "str"}
        parameters = self._normalize_properties(schema_like)
        return {"name": name, "description": description, "parameters": parameters}

    def _validate_function(self, access_token: str, function_obj: Dict) -> Dict:
        url = "https://gigachat.devices.sberbank.ru/api/v1/functions/validate"
        headers = {"Content-Type": "application/json", "Authorization": f"Bearer {access_token}", "Accept": "application/json"}
        resp = requests.post(url, headers=headers, json=function_obj, timeout=30, verify=False)
        # не падаем по статусу — возвращаем текст диагностики
        try:
            data = resp.json()
        except Exception:
            data = {"status_code": resp.status_code, "text": resp.text[:500]}
        return data

    def build_output(self) -> Data:
        temperature = float(self.temperature)
        access_token = self._get_access_token(self._get_text(self.giga_api), self.scope)

        fn = self._build_function(self.function_name, self.function_description, self.output_schema)
        validation = self._validate_function(access_token, fn)  # полезно для отладки
        # можно проверить warnings и подсказать пользователю

        url = "https://gigachat.devices.sberbank.ru/api/v1/chat/completions"
        headers = {"Content-Type": "application/json", "Authorization": f"Bearer {access_token}", "Accept": "application/json"}
        payload = {
            "model": self.model_name,
            "messages": [
                {"role": "system", "content": self.system_prompt},
                {"role": "user", "content": self.input_message},
            ],
            "temperature": temperature,
            "function_call": {"name": self.function_name},
            "functions": [fn],
            # "stream": False,  # при необходимости
        }

        try:
            resp = requests.post(url, headers=headers, json=payload, timeout=60, verify=False)
            # вместо raise_for_status читаем тело ошибки, чтобы увидеть причину 422
            try:
                body = resp.json()
            except Exception:
                body = {"status_code": resp.status_code, "text": resp.text[:1000]}

            if resp.status_code != 200:
                return Data(data={"error": body, "validation": validation}, text=json.dumps({"error": body, "validation": validation}, ensure_ascii=False, indent=2))

            choice0 = (body.get("choices") or [{}])[0]
            msg = choice0.get("message", {})
            fn_call = msg.get("function_call", {})
            args = fn_call.get("arguments", {})
            return Data(data=args)

        except Exception as e:
            return Data(data={"error": str(e)}, text=f"Error: {str(e)}")

